#include &lt;iostream&gt;#include &lt;cstdio&gt;#include &lt;cstring&gt;#define ll long longusing namespace std;const int INF = 0x3f3f3f3f;const int maxn = 30005;int s[maxn],height[maxn];void init_set(int tot)&#123; // åˆå§‹åŒ–å¹¶æŸ¥é›†    for(int i=0;i&lt;=tot;++i)&#123;        s[i] = i;        height[i] = 0;    &#125;&#125;int find_set(int x)&#123; // è·¯å¾„å‹ç¼©çš„æŸ¥è¯¢    if(x != s[x]) s[x] = find_set(s[x]);    return s[x];&#125;void union_set(int x,int y)&#123; // æ ¹æ®æ ‘é«˜åº¦è¿›è¡Œåˆå¹¶    x = find_set(x); y = find_set(y);    if(height[x] == height[y])&#123;        height[x] += 1;        s[y] = x;    &#125;    else if(height[x]&gt;height[y])        s[y] = x;    else        s[x] = y;&#125;int main()&#123;    while(1)&#123;        int n,m;        cin &gt;&gt; n &gt;&gt; m;        if(n == 0 &amp;&amp; m == 0) break;        init_set(n);        while(m--)&#123;            int num;            cin &gt;&gt; num;            int pre_peo;            for(int i=1;i&lt;=num;++i)&#123;                int now_peo;                cin &gt;&gt; now_peo;                if(i == 1) // ç¬¬ä¸€ä¸ªäººæ²¡æœ‰å‰ç½®ï¼Œæ‰€ä»¥å°±å®‰æ’ä»–è‡ªå·±                    pre_peo = now_peo;                union_set(pre_peo,now_peo);                pre_peo = now_peo;            &#125;        &#125;        int res = 1; // 0è‡ªå·±ä¹Ÿç®—åœ¨å…¶ä¸­ï¼Œæ‰€ä»¥ä»1å¼€å§‹è®¡æ•°        int suspect = find_set(0);        for(int i=1;i&lt;n;++i)&#123;            if(find_set(i) == suspect)res++;         &#125;        cout &lt;&lt; res &lt;&lt; endl;    &#125;    return 0;&#125;
