æºç #include &lt;iostream&gt;#include &lt;cstdio&gt;#include &lt;cstring&gt;#include &lt;cmath&gt;#define ll long longusing namespace std;const double eps = 1e-5;const int INF = 0x3f3f3f3f;const int maxn = 1005;int n,d,cnt;int s[maxn],height[maxn],flag[maxn],vis[maxn];struct point&#123;    int x,y;&#125;a[maxn];void init_set(int tot)&#123; // åˆå§‹åŒ–å¹¶æŸ¥é›†    for(int i=0;i&lt;=tot;++i)&#123;        s[i] = i;        height[i] = 0;        flag[i] = 0;    &#125;&#125;int find_set(int x)&#123; // è·¯å¾„å‹ç¼©çš„æŸ¥è¯¢    if(x != s[x]) s[x] = find_set(s[x]);    return s[x];&#125;void union_set(int x,int y)&#123; // æ ¹æ®æ ‘é«˜åº¦è¿›è¡Œåˆå¹¶    x = find_set(x); y = find_set(y);    if(height[x] == height[y])&#123;        height[x] += 1;        s[y] = x;    &#125;    else if(height[x]&gt;height[y])        s[y] = x;    else        s[x] = y;&#125;void connect(int x)&#123;    for(int i=0;i&lt;cnt;++i)&#123;        double dis = sqrt((double)(pow((a[x].x-a[vis[i]].x),2)+pow((a[x].y-a[vis[i]].y),2)));        if(dis-d&lt;=eps)            union_set(x,vis[i]);    &#125;&#125;int main()&#123;    cin &gt;&gt; n &gt;&gt; d;    init_set(maxn);    for(int i=1;i&lt;=n;++i)        cin &gt;&gt; a[i].x &gt;&gt; a[i].y;    char ope;    while(~scanf(&quot;%c&quot;,&amp;ope))&#123;        if(ope == &#x27;O&#x27;)&#123;            int now;            cin &gt;&gt; now;            connect(now);            flag[now] = 1;            vis[cnt++] = now;        &#125;        if(ope == &#x27;S&#x27;)&#123;            int test_a,test_b;            cin &gt;&gt; test_a &gt;&gt; test_b;            if(flag[test_b]&amp;&amp;flag[test_a]&amp;&amp;find_set(test_a) == find_set(test_b))                cout &lt;&lt; &quot;SUCCESS&quot; &lt;&lt; endl;            else                cout &lt;&lt; &quot;FAIL&quot; &lt;&lt; endl;        &#125;    &#125;    return 0;&#125;
